import React,{ useState }  from 'react'
// import React, { useState } from 'react'
import './App.css'
import NavbarComponent from './navbar'
import LandingPage from './landingPage'
import FooterComponent from './footer'
import LoginAuth from './loginAuth'

function App(props){

    const [view, setView] = useState("none")
    const [translate, setTranslate] = useState('translateX(0px)')
    const [bodyBlur, setBodyBlur] = useState('blur(0px)')
    const [loginBlur, setLoginBlur] = useState('blur(0px)')


    const loginAuthPage = () => {
        setView((prevView) => (prevView === 'none' ? 'flex' : 'none'));
        setTranslate((prevMove) => (prevMove=== 'translateX(0px)' ? 'translateX(540px)' : 'translateX(0px)'));
        setBodyBlur((prevBody) => (prevBody=== 'blur()' ? 'blur()x)' : 'blur()'));
        setLoginBlur((prevLogin) => (prevLogin=== 'blur()' ? 'blur()x)' : 'blur()'));       
    };

    return(
        <>
            <div className='main-body-wrapper' style={{filter: bodyBlur}}>
                <div className='navbar-component-wrapper'>
                    <NavbarComponent loginAuthPage={loginAuthPage}/>
                </div>
                <div className='landing-home-page'>
                    <LandingPage/>
                </div>
                <div className='landing-page-footer-container'>
                        <FooterComponent/>
                </div>
                <div className='login-auth-page-wrapper' style={{transform: translate, filter: loginBlur}} >
                    <LoginAuth disp={view}></LoginAuth>
                </div>  
            </div> 

        </>
    )
}

export default App;
