import React,{ useState }  from 'react'
// import React, { useState } from 'react'
import './App.css'
import NavbarComponent from './navbar'
import LandingPage from './landingPage'
import LoginAuth from './loginAuth'
import SignUpAuth from'./signUpAuth'

function App(props){

    const [view, setView] = useState("none")
    const [viewSignUp, setViewSignUp] = useState("none")
    const [translate, setTranslate] = useState('translateX(0px)')
    const [loginBlur, setLoginBlur] = useState('blur(5px)')
    const [bodyBlur, setBodyBlur] = useState('blur(0px)')
    const [visi, setVisi] = useState('0')

    
    const loginAuthPage = () => {
        setView((prevView) => (prevView === 'none' ? 'flex' : 'none'));
        setTranslate((prevMove) => (prevMove=== 'translateX(0px)' ? 'translateX(540px)' : 'translateX(0px)'));
        setLoginBlur((prevLogin) => (prevLogin=== 'blur(5px)' ? 'blur(0px)' : 'blur(5px)'));       
        setBodyBlur((prevBody) => (prevBody=== 'blur(0px)' ? 'blur(10px)' : 'blur(0px)'));
        
    };

    const signUpAuthPage = () => {
        setViewSignUp((prevViewSignUp) => (prevViewSignUp === 'none' ? 'flex' : 'none'));
        setTranslate((prevMove) => (prevMove=== 'translateX(0px)' ? 'translateX(540px)' : 'translateX(0px)'));
        setLoginBlur((prevLogin) => (prevLogin=== 'blur(5px)' ? 'blur(0px)' : 'blur(5px)'));       
        setBodyBlur((prevBody) => (prevBody=== 'blur(0px)' ? 'blur(10px)' : 'blur(0px)'));
        
    }
    
    // const closeAuthPage = () => {
    //     setView((prevNew) => (prevNew === 'flex' ? 'none' : 'flex'));
    //     // setView('none')
    //     // console.log("done")
    // }

    return(
        <>
            <div className='main-body-wrapper'>
                <div className='navbar-component-wrapper' style={{filter: bodyBlur}}>
                    <NavbarComponent loginAuthPage={loginAuthPage} signUpAuthPage={signUpAuthPage}/>
                </div>
                <div className='landing-home-page' style={{filter: bodyBlur}}>
                    <LandingPage/>
                </div>
                <div className='login-auth-page-wrapper' style={{transform: translate, filter: loginBlur}} >
                    <LoginAuth disp={view}></LoginAuth>
                </div>  
                <div className='signup-auth-page-wrapper' style={{transform: translate, filter: loginBlur, zIndex: visi}} >
                    <SignUpAuth disp={viewSignUp}></SignUpAuth>
                </div>  
            </div> 

        </>
    )
}

export default App;
