import React,{ useState }  from 'react'
// import React, { useState } from 'react'
import './App.css'
import NavbarComponent from './navbar'
import LandingPage from './landingPage'
import FooterComponent from './footer'
import LoginAuth from './loginAuth'

function App(){

    const [view, setView] = useState("none")
    const [translate, setTranslate] = useState('translateX(540px)')


    const loginAuthPage = () => {
        setView((prevView) => (prevView === 'none' ? 'flex' : 'none'));
        // setShake((prevMove) => (prevMove=== 'translateX(0px)' ? 'translateX(540px)' : 'translateX(0px)'));

        
    };
    const toggleTranslate = () => {
        // Toggle between 'translateX(0px)' and 'translateX(100px)'
        setTranslate((prevTranslate) => (prevTranslate === 'translateX(0px)' ? 'translateX(540px)' : 'translateX(0px)'));
    };

    return(
        <>
            <div className='main-body-wrapper'>
                <div className='navbar-component-wrapper'>
                    <NavbarComponent loginAuthPage={loginAuthPage} toggleTranslate={toggleTranslate}/>
                </div>
                <div className='landing-home-page'>
                    <LandingPage/>
                </div>
                <div className='landing-page-footer-container'>
                        <FooterComponent/>
                </div>
                <div className='login-auth-page-wrapper' style={{transform: `trnaslate(${props.move})`}} >
                    <LoginAuth disp={view}></LoginAuth>
                </div>  
            </div> 

        </>
    )
}

export default App;
